#$diff = random(2,5,1);   
$diff = 5;    #---COMMENT OUT THIS LINE FOR RANDOMIZATION

#$growthRate = random(2,3,1);
$growthRate = 2;   #---COMMENT OUT THIS LINE FOR RANDOMIZATION
#$h0 = random(3,8,1);
$h0 = 6;     #---COMMENT OUT THIS LINE FOR RANDOMIZATION
$h4 = $h3 + $diff*$growthRate;
Context()->variables->add(t=>"Real",h=>"Real");
$f = Formula("6+2*t")->reduce;
$displayEq = "h = $growthRate t + $h0";   

#$Tweek=random(2,5);
$Tweek = 3;
$Tdays = $Tweek*7;
$H = $f->eval(t=>$Tdays);  
$height = FormulaWithUnits(
"$H in");

$Hft = 6;
$Hinches = $Hft*12;
$Hdiff = $Hinches-$h0;
$time0=($Hdiff)/$growthRate;
$time = NumberWithUnits(
"$time0 day");

