return $h+$a*sinh($t); };
$yfunc[6] = sub { my $t = shift();
return $k -$b*cosh($t); };
$xfunc[7] = sub { my $t = shift();
return $h + $b*sinh($t); };
$yfunc[7] = sub { my $t = shift();
return $k-$a*cosh($t); };

foreach my $i (0..3) {
