webwork.pg-code:
Context()->flags->set( reduceConstants => 0, reduceConstantFunctions => 0,   );
$r1 = Formula("sqrt(2)");
$r2 = Formula("-sqrt(2)");
$r3 = Formula("sqrt(3)");
$r4 = Formula("-sqrt(3)");
$answer = Formula("$r1, $r2, $r3, $r4");
$my_evaluator = $answer -> cmp(
 showHints=>1,
 showLengthHints => 1,
 partialCredit => 1,
);

Context()->flags->set(useBaseTenLog=>1);
Context()->flags->set( reduceConstants => 0, reduceConstantFunctions => 0,   );

$r1 = Compute("0");
$r2 = Formula("log(2)");
$answer = Formula("$r1, $r2");
$my_evaluator = $answer -> cmp(
 showHints=>1,
 showLengthHints => 1,
 partialCredit => 1,
);
